var dayLabelCache = new Map();
export function getDateLabel(locale, date) {
  var cacheKey = locale + date.getTime();
  var cachedValue = dayLabelCache.get(cacheKey);

  if (cachedValue) {
    return cachedValue;
  }

  var value = date.toLocaleDateString(locale, {
    weekday: 'long',
    month: 'long',
    day: 'numeric',
    year: 'numeric'
  });
  dayLabelCache.set(cacheKey, value);
  return value;
}
export function renderTimeLabel(locale, date, format) {
  var options = {};

  if (format === 'hh') {
    options = {
      hour: '2-digit'
    };
  }

  if (format === 'hh:mm') {
    options = {
      hour: '2-digit',
      minute: '2-digit'
    };
  }

  var value = date.toLocaleTimeString(locale, options);
  return value;
}
